import styled from "styled-components";
import { useState } from "react";

import { ChevronLeft, ChevronRight } from "@material-ui/icons";

import img from "./../img/gals/gal1.png";
import img2 from "./../img/gals/gal2.png";
import img3 from "./../img/gals/gal3.png";

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
`;

const Arrow = styled.div`
  // We can write JS inside of CSS using styled components, F**KING AMAZING
  width: 50px;
  height: 50px;
  background-color: #f8f9fa;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${(props) =>
    props.direction === "left" &&
    "10px"}; // the direction definied below is a prop, that's why we say props.direction
  right: ${(props) =>
    props.direction === "right" &&
    "10px"}; // the direction definied below is a prop, that's why we say props.direction
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
  transition: 0.3s all;
  &:hover {
    opacity: 0.7;
  }
  z-index: 2;
`;

const Wrapper = styled.div`
  height: 100%;
  display: flex;
  transform: translateX(0vw);
`;

const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
  background-color: ${(props) =>
    props.bg}; // We can use a function in css tooo!, we are passing in the background color as a prop
`;
const ImgContainer = styled.div`
  flex: 1;
  height: 100%; // 100% means the parents 100% height
`;

const Image = styled.img`
  height: 85%;
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 50px;
`;

const Title = styled.h1`
  font-size: 85px;
  letter-spacing: 2px;
`;
const Desc = styled.p`
  margin: 50px 0px;
  font-size: 25px;
  font-weight: 500;
  letter-spacing: 3px;
`;
const Button = styled.button`
  padding: 10px;
  font-size: 25px;
  border: none;
  background-color: transparent;
  cursor: pointer;
  border: 1px solid gray;
  border-radius: 10px;
  transition: color 0.3s ease-in-out, box-shadow 0.3s ease-in-out;
  box-shadow: inset 0 0 0 0 #868e96;

  &:hover {
    box-shadow: inset 150px 0 0 0 #868e96;
    color: white;
  }
`;

const Slider = () => {
  const [slideIndex, setSlideIndex] = useState(0);
  const handleClick = (direction) => {};

  return (
    <div>
      <Container>
        <Arrow direction="left" onClick={() => handleClick("left")}>
          <ChevronLeft />
        </Arrow>
        <Wrapper>
          <Slide bg="#f8f9fa">
            <ImgContainer>
              <Image src={img} />
            </ImgContainer>
            <InfoContainer>
              <Title>Beat The Heat!</Title>
              <Desc>
                This Summer, Beat The Heat With <b>Style</b> And 50% Off On
                Summer Clothing
              </Desc>
              <Button>Shop Now!</Button>
            </InfoContainer>
          </Slide>
          <Slide bg="#868e96">
            <ImgContainer>
              <Image src={img2} />
            </ImgContainer>
            <InfoContainer>
              <Title style={{ fontSize: "80px" }}>
                Be Trendiest of them All!
              </Title>
              <Desc>
                This Summer, Be the most Trendy version of Yourself with our All
                new Summer Collection
              </Desc>
              <Button>Get Now!</Button>
            </InfoContainer>
          </Slide>
          <Slide bg="#868e96">
            <ImgContainer>
              <Image src={img3} style={{ marginLeft: "10px" }} />
            </ImgContainer>
            <InfoContainer>
              <Title>Create, Follow, Inspire</Title>
              <Desc>
                Don't like the Ordinary?, Want something new?, Start a Fashion
                Revolution with our new set of In-house Clothing
              </Desc>
              <Button>Shop Now!</Button>
            </InfoContainer>
          </Slide>
        </Wrapper>
        <Arrow direction="right" onClick={() => handleClick("right")}>
          <ChevronRight />
        </Arrow>
      </Container>
    </div>
  );
};

export default Slider;
