import {
  FavoriteBorderOutlined,
  SearchOutlined,
  ShoppingCartOutlined,
} from "@material-ui/icons";
import styled from "styled-components";
import "./product.css";

const Container = styled.div`
  flex: 1;
  margin: 5px;
  min-width: 280px;
  min-height: 350px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #f5fbfd;
  position: relative;
  max-height: 500px;
  transition: transform 0.5s;
  transform-style: preserve-3d;
`;

const Circle = styled.div`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  background-color: #fff;
  position: absolute;
`;
const Image = styled.img`
  height: 75%;
  object-fit: cover;
  z-index: 2;
`;
const Info = styled.div`
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  position: absolute;
  background-color: rgba(0, 0, 0, 0.2);
  z-index: 3;
  display: flex;
  align-items: center;
  justify-content: center;
  display: none;
`;
const Icon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  align-items: center;
  justify-content: center;
  margin: 10px;
  gap: 15px;
  background-color: #fff;
  display: flex;
  transition: all 0.3s ease;
  &:hover {
    background-color: #e9f5f5;
    transform: scale(1.1);
  }
  display: none;
`;

const Product = ({ item }) => {
  return (
    <Container className="product--flip">
      <Circle />
      <Image src={item.img} />
      <Info className="info">
        <Icon className="icon">
          <ShoppingCartOutlined />
        </Icon>
        <Icon className="icon">
          <SearchOutlined />
        </Icon>
        <Icon className="icon">
          <FavoriteBorderOutlined />
        </Icon>
      </Info>
    </Container>
  );
};

export default Product;
