import styled from "styled-components";

const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(#3b5bdb5e, #228be665),
    url("https://ik.imagekit.io/anik/42_11_Pu89mDJPR.jpg?ik-sdk-version=javascript-1.4.3&updatedAt=1649673134878")
      center;

  display: flex;
  align-items: center;
  justify-content: center;
`;

const Wrapper = styled.div`
  width: 40%;
  padding: 30px;
  background-color: white;
  display: flex;
  gap: 40px;
  border-radius: 10px;
`;

const Title = styled.h1`
  font-size: 40px;
  font-weight: 500;
`;

const Form = styled.form`
  display: flex;
  flex-wrap: wrap;
`;

const Line = styled.div`
  height: 40vh;
  border: 1px solid teal;
`;

const Input = styled.input`
  flex: 1;
  min-width: 40%;
  margin: 20px 10px 0px 0px;
  padding: 10px;
  max-width: 80%;
  border-radius: 10px;
  border: none;

  &:focus {
    outline: none;
  }
`;

const Agreement = styled.span`
  font-size: 12px;
  margin: 20px 0px;
`;

const Button = styled.button`
  width: 20%;
  border: none;
  padding: 10px;
  background-color: #099268;
  color: white;
  cursor: pointer;
  border-radius: 5px;
  transition: 0.3s all;
  &:hover {
    background-color: #087f5b;
  }
`;

const Login = () => {
  return (
    <Container>
      <Wrapper>
        <Title>Sign In</Title>
        <Line />
        <Form>
          <Input placeholder="Email" />
          <Input placeholder="Password" />
          <Button>Sign In</Button>
        </Form>
      </Wrapper>
    </Container>
  );
};

export default Login;
