import styled from "styled-components";

const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(#3b5bdb5e, #228be665),
    url("https://ik.imagekit.io/anik/42_11_Pu89mDJPR.jpg?ik-sdk-version=javascript-1.4.3&updatedAt=1649673134878")
      center;

  display: flex;
  align-items: center;
  justify-content: center;
`;

const Wrapper = styled.div`
  width: 40%;
  padding: 20px;
  background-color: white;
  display: flex;
  gap: 40px;
`;

const Title = styled.h1`
  font-style: 24px;
  font-weight: 300;
`;

const Form = styled.form`
  display: flex;
  flex-wrap: wrap;
`;

const Input = styled.input`
  flex: 1;
  min-width: 40%;
  margin: 20px 10px 0px 0px;
  padding: 10px;
  max-width: 80%;
  border-radius: 10px;
  border: none;

  &:focus {
    outline: none;
  }
`;

const Agreement = styled.span`
  font-size: 12px;
  margin: 20px 0px;
`;

const Button = styled.button`
  width: 30%;
  border: none;
  padding: 15px 20px;
  background-color: teal;
  color: white;
  cursor: pointer;
  border-radius: 5px;
`;

const Register = () => {
  return (
    <Container>
      <Wrapper>
        <Title>Create An Account with Us</Title>
        <Form>
          <Input placeholder="First Name" />
          <Input placeholder="Last Name" />
          <Input placeholder="Email" />
          <Input placeholder="Username" />
          <Input placeholder="Password" />
          <Input placeholder="Confirm Password" />
          <Agreement>
            By Creating an account with us, You are accepting our{" "}
            <b>Terms of Service</b> and our <b>Privacy Policy</b>
          </Agreement>
          <Button>Sign Up!</Button>
        </Form>
      </Wrapper>
    </Container>
  );
};

export default Register;
