import styled from "styled-components";
import { Facebook, Instagram, Twitter } from "@material-ui/icons";

const Container = styled.div`
  display: flex;
  height: 40vh;
`;
const Left = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const Logo = styled.h1`
  text-align: center;
`;
const Desc = styled.h1`
  margin: 20px 0px;
  font-weight: 500;
  text-align: center;
  font-size: 24px;
`;

const ReachUs = styled.p`
  text-align: center;
`;

const SocialContainer = styled.h1`
  display: flex;
  justify-content: space-between;
  width: 30%;
  align-self: center;
`;
const SocialIcon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  color: white;
  background-color: ${(props) => props.color};
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Center = styled.div`
  flex: 1;
  padding: 20px;
`;

const Title = styled.h3`
  margin-bottom: 30px;
  text-align: center;
`;

const List = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 40px;
`;

const ListItem = styled.li`
  width: 120px;
  font-weight: 500;
  text-align: center;
`;

const Right = styled.div`
  flex: 1;
  padding: 20px;
`;

const Footer = () => {
  return (
    <Container>
      <Left>
        <Logo>Phalnax</Logo>
        <Desc>
          We frimly Believe that Style and Elegance should go hand in hand, And
          We work to make that happen, In the most simple and profound way
          possible
        </Desc>
        <ReachUs>You can reach us on</ReachUs>
        <SocialContainer>
          <SocialIcon color="#339af0">
            <Facebook />
          </SocialIcon>
          <SocialIcon color="#fd7e14">
            <Instagram />
          </SocialIcon>
          <SocialIcon color="#228be6">
            <Twitter />
          </SocialIcon>
        </SocialContainer>
      </Left>
      <Center>
        <Title>Helpful Links</Title>
        <List>
          <ListItem>Home</ListItem>
          <ListItem>Cart</ListItem>
          <ListItem>Men Fashion</ListItem>
          <ListItem>Women Fashion</ListItem>
          <ListItem>Jewels</ListItem>
          <ListItem>My Account</ListItem>
          <ListItem>Orders</ListItem>
          <ListItem>Gift</ListItem>
          <ListItem>Gift Cards</ListItem>
          <ListItem>Decoration</ListItem>
          <ListItem>In-House</ListItem>
          <ListItem>ToS</ListItem>
        </List>
      </Center>
      <Right></Right>
    </Container>
  );
};

export default Footer;
