import styled from "styled-components";

const Container = styled.div`
  flex: 1;
  margin: 3px;
  height: 70vh;
  position: relative;
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  transition: 0.3s all;
  &:hover {
    -webkit-box-shadow: 7px 6px 23px 5px rgba(0, 0, 0, 0.58);
    box-shadow: 7px 6px 23px 5px rgba(0, 0, 0, 0.58);
  }
`;
const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover; // this is basically corp these images
  &:hover {
    filter: blur(4px);
  }
`;
const Info = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;
const Title = styled.h1`
  color: #f8f9fa;
  margin-bottom: 20px;
`;
const Button = styled.button`
  border: none;
  padding: 10px;
  background-color: #f1f3f5;
  color: gray;
  cursor: pointer;
  font-weight: 600;
  border-radius: 10px;
`;
const CategoryItem = ({ item }) => {
  return (
    <Container style={{ backgroundColor: item.bg }}>
      <Image src={item.img} />
      <Info>
        <Title>{item.title}</Title>
        <Button>Buy Now!</Button>
      </Info>
    </Container>
  );
};

export default CategoryItem;
