import styled from "styled-components";

import { ChevronLeft, ChevronRight } from "@material-ui/icons";

import img from "./../img/gals/gal1.png";

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  background-color: aqua;
  position: relative;
`;

const Arrow = styled.div`
  // We can write JS inside of CSS using styled components, F**KING AMAZING
  width: 50px;
  height: 50px;
  background-color: #f8f9fa;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${(props) =>
    props.direction === "left" &&
    "10px"}; // the direction definied below is a prop, that's why we say props.direction
  right: ${(props) =>
    props.direction === "right" &&
    "10px"}; // the direction definied below is a prop, that's why we say props.direction
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
  transition: 0.3s all;
  &:hover {
    opacity: 0.7;
  }
`;

const Wrapper = styled.div`
  height: 100%;
`;

const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
`;
const ImgContainer = styled.div`
  flex: 1;
  height: 100%; // 100% means the parents 100% height
`;

const Image = styled.img``;

const InfoContainer = styled.div`
  flex: 1;
`;

const Slider = () => {
  return (
    <div>
      <Container>
        <Arrow direction="left">
          <ChevronLeft />
        </Arrow>
        <Wrapper>
          <Slide>
            <ImgContainer>
              <Image src={img} />
            </ImgContainer>
            <InfoContainer></InfoContainer>
          </Slide>
        </Wrapper>
        <Arrow direction="right">
          <ChevronRight />
        </Arrow>
      </Container>
    </div>
  );
};

export default Slider;
